import { useUser } from "@clerk/clerk-react";

interface MessageProps {
  sender: string;
  content: string;
  timestamp: Date;
}

const Message: React.FC<MessageProps> = ({ sender, content, timestamp }) => {
  const { user } = useUser();
  const isCurrentUser = user?.id === sender;

  return (
    <>
      {isCurrentUser ? (
        <div className="flex justify-end items-start gap-2.5">
          <div className="flex flex-col gap-1 w-full max-w-[320px] items-end">
            <div className="flex items-center space-x-2 space-x-reverse">
              <span className="text-sm font-normal text-gray-500">11:46</span>
              <span className="text-sm font-semibold text-gray-900">
                Bonnie Green
              </span>
            </div>
            <div className="flex flex-col leading-1.5 p-4 border-gray-200 bg-gray-100 rounded-s-xl rounded-se-xl">
              <p className="text-sm font-normal text-gray-900">
                {" "}
                That's awesome. I think our users will really appreciate the
                improvements.
              </p>
            </div>
            <span className="text-sm font-normal text-gray-500">
              Delivered
            </span>
          </div>
        </div>
      ) : (
        <li className="max-w-lg flex gap-x-2 sm:gap-x-4 me-11">
          <img
            className="inline-block h-9 w-9 rounded-full"
            src={user?.imageUrl}
            alt="Image Description"
          />

          <div className="bg-white border border-gray-200 rounded-tl-sm rounded-tr-lg rounded-br-lg rounded-bl-lg p-3 space-y-3">
            <p className="text-sm text-gray-800">{content}</p>
          </div>
        </li>
      )}
    </>
  );
};

export default Message;
