import { useUser } from "@clerk/clerk-react";

interface MessageProps {
  sender: string;
  content: string;
  timestamp: Date;
}

const Message: React.FC<MessageProps> = ({ sender, content, timestamp }) => {
  const { user } = useUser();
  const isCurrentUser = user?.id === sender;

  return (
    <>
      {isCurrentUser ? (
        <CurrentUserMessage content={content} />
      ) : (
        <OtherUserMessage content={content} />
      )}
    </>
  );
};

function CurrentUserMessage(content: string) {
  return (
    <li className="flex ms-auto gap-x-2 sm:gap-x-4">
      <div className="grow text-end space-y-3">
        <div className="inline-block bg-blue-600 rounded-2xl p-4 shadow-sm">
          <p className="text-sm text-white">{content}</p>
        </div>
      </div>
      <span className="flex-shrink-0 inline-flex items-center justify-center h-[2.375rem] w-[2.375rem] rounded-full bg-gray-600">
        <span className="text-sm font-medium text-white leading-none">AZ</span>
      </span>
    </li>
  );
}

function OtherUserMessage(content: string) {
  return (
    <li className="max-w-lg flex gap-x-2 sm:gap-x-4 me-11">
      <img
        className="inline-block h-9 w-9 rounded-full"
        src="https://images.unsplash.com/photo-1541101767792-f9b2b1c4f127?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&&auto=format&fit=facearea&facepad=3&w=300&h=300&q=80"
        alt="Image Description"
      />

      <div className="bg-white border border-gray-200 rounded-2xl p-4 space-y-3">
        <p className="text-sm text-gray-800">{content}</p>
      </div>
    </li>
  );
}

export default Message;
