import { Fragment, useEffect, useState } from "react";
import { Dialog, Transition } from "@headlessui/react";
import { Bars3Icon, PlusIcon, XMarkIcon } from "@heroicons/react/24/outline";
import { UserButton } from "@clerk/clerk-react";
import CreateChannelComponent from "./CreateChannelComponent";
import {
  QuerySnapshot,
  collection,
  getDocs,
  onSnapshot,
  orderBy,
  query,
} from "firebase/firestore";
import db from "../services/firebaseConfig";

const channels = [
  { id: 1, name: "tl:dr", href: "#", current: false },
  { id: 2, name: "daily-news", href: "#", current: false },
  { id: 3, name: "workation", href: "#", current: false },
];

function classNames(...classes: string[]) {
  return classes.filter(Boolean).join(" ");
}

export default function Sidebar() {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [channels, setChannels] = useState<{ channelId: string; channelName: string; }[]>([]);

  useEffect(() => {
    const messagesCollection = collection(db, "channels");
    const messagesQuery = query(messagesCollection, orderBy("timestamp"));

    // Fetch all channels once on component mount
    const fetchMessages = async () => {
      getDocs(messagesQuery).then((snapshot: QuerySnapshot) => {
        const initialChannels = snapshot.docs.map((doc) => ({
          channelId: doc.data().id,
          channelName: doc.data().name,
        }));
        setChannels(initialChannels);
      });
    };

    // Subscribe to channels after initial fetch
    const subscribeToMessages = () => {
      onSnapshot(messagesQuery, (snapshot: QuerySnapshot) => {
        const updatedMessages = snapshot.docs.map((doc) => ({
          content: doc.data().content,
          senderId: doc.data().senderId,
          senderUsername: doc.data().senderUsername,
          timestamp: doc.data().timestamp.toDate().toLocaleTimeString([], {
            hour: "numeric",
            minute: "2-digit",
            hour12: true,
          }),
        }));
        setMessages(updatedMessages);
      });
    };

    fetchMessages();
    const unsubscribe = subscribeToMessages();

    // Cleanup subscription on component unmount
    return () => unsubscribe;
  }, []);

  return (
    <>
      <Transition.Root show={sidebarOpen} as={Fragment}>
        <Dialog
          as="div"
          className="relative z-50 lg:hidden"
          onClose={setSidebarOpen}
        >
          <Transition.Child
            as={Fragment}
            enter="transition-opacity ease-linear duration-300"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="transition-opacity ease-linear duration-300"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-gray-900/80" />
          </Transition.Child>

          <div className="fixed inset-0 flex">
            <Transition.Child
              as={Fragment}
              enter="transition ease-in-out duration-300 transform"
              enterFrom="-translate-x-full"
              enterTo="translate-x-0"
              leave="transition ease-in-out duration-300 transform"
              leaveFrom="translate-x-0"
              leaveTo="-translate-x-full"
            >
              <Dialog.Panel className="relative mr-16 flex w-full max-w-xs flex-1">
                <Transition.Child
                  as={Fragment}
                  enter="ease-in-out duration-300"
                  enterFrom="opacity-0"
                  enterTo="opacity-100"
                  leave="ease-in-out duration-300"
                  leaveFrom="opacity-100"
                  leaveTo="opacity-0"
                >
                  <div className="absolute left-full top-0 flex w-16 justify-center pt-5">
                    <button
                      type="button"
                      className="-m-2.5 p-2.5"
                      onClick={() => setSidebarOpen(false)}
                    >
                      <span className="sr-only">Close sidebar</span>
                      <XMarkIcon
                        className="h-6 w-6 text-white"
                        aria-hidden="true"
                      />
                    </button>
                  </div>
                </Transition.Child>
                {/* Sidebar component, swap this element with another sidebar if you like */}
                <div className="flex grow flex-col gap-y-5 overflow-y-auto bg-white px-6 pb-2">
                  <div className="flex h-16 shrink-0 items-center">
                    <img
                      className="h-8 w-auto"
                      src="/xyz-logo.svg"
                      alt="Your Company"
                    />
                  </div>
                  <nav className="flex flex-1 flex-col">
                    <ul role="list" className="flex flex-1 flex-col gap-y-7">
                      <li>
                        <div className="text-xs font-semibold leading-6 text-gray-400">
                          Live Channels
                        </div>
                        <ul role="list" className="-mx-2 mt-2 space-y-1">
                          {channels.map((channel) => (
                            <li key={channel.name}>
                              <a
                                href={channel.href}
                                className={classNames(
                                  channel.current
                                    ? "bg-gray-50 text-indigo-600"
                                    : "text-gray-700 hover:text-indigo-600 hover:bg-gray-50",
                                  "group flex gap-x-3 rounded-md p-2 text-sm leading-6 font-semibold"
                                )}
                              >
                                <span className="truncate">{channel.name}</span>
                              </a>
                            </li>
                          ))}
                          <a className="text-indigo-600 group flex gap-x-3 rounded-md p-2 text-sm leading-6 font-semibold mt-8">
                            <PlusIcon className="text-indigo-600 h-6 w-6 shrink-0" />
                            Create a channel
                          </a>
                        </ul>
                      </li>
                    </ul>
                  </nav>
                </div>
              </Dialog.Panel>
            </Transition.Child>
          </div>
        </Dialog>
      </Transition.Root>

      {/* Static sidebar for desktop */}
      <div className="hidden lg:fixed lg:inset-y-0 lg:z-50 lg:flex lg:w-72 lg:flex-col">
        {/* Sidebar component, swap this element with another sidebar if you like */}
        <div className="flex grow flex-col gap-y-5 overflow-y-auto border-r border-gray-200 bg-white px-6">
          <div className="flex h-16 shrink-0 items-center">
            <img
              className="h-8 w-auto"
              src="/xyz-logo.svg"
              alt="Your Company"
            />
          </div>
          <nav className="flex flex-1 flex-col">
            <ul role="list" className="flex flex-1 flex-col gap-y-7">
              <li>
                <div className="text-xs font-semibold leading-6 text-gray-400">
                  Live Channels
                </div>
                <ul role="list" className="-mx-2 mt-2 space-y-1">
                  {channels.map((channel) => (
                    <li key={channel.name}>
                      <a
                        href={channel.href}
                        className={classNames(
                          channel.current
                            ? "bg-gray-50 text-indigo-600"
                            : "text-gray-700 hover:text-indigo-600 hover:bg-gray-50",
                          "group flex gap-x-3 rounded-md p-2 text-sm leading-6 font-semibold"
                        )}
                      >
                        <span className="truncate">{channel.name}</span>
                      </a>
                    </li>
                  ))}
                  <li>
                    <CreateChannelComponent />
                  </li>
                </ul>
              </li>
              <li className="-mx-6 mt-auto">
                <div className="relative">
                  <div className="w-full flex items-center p-6">
                    <UserButton afterSignOutUrl="/sign-in" />
                  </div>
                </div>
              </li>
            </ul>
          </nav>
        </div>
      </div>

      <div className="sticky top-0 z-40 flex items-center gap-x-6 bg-white px-4 py-4 shadow-sm sm:px-6 lg:hidden">
        <button
          type="button"
          className="-m-2.5 p-2.5 text-gray-700 lg:hidden"
          onClick={() => setSidebarOpen(true)}
        >
          <span className="sr-only">Open sidebar</span>
          <Bars3Icon className="h-6 w-6" aria-hidden="true" />
        </button>
        <div className="flex-1 text-sm font-semibold leading-6 text-gray-900">
          Dashboard
        </div>
      </div>
    </>
  );
}
