import { useUser } from "@clerk/clerk-react";

interface MessageProps {
  sender: string;
  content: string;
  timestamp: Date;
}

const Message: React.FC<MessageProps> = ({ sender, content, timestamp }) => {
  const { user } = useUser();
  const isCurrentUser = user?.id === sender;

  return (
    <>
      {isCurrentUser ? (
<div class="flex items-start gap-2.5">
   <div class="flex flex-col gap-1 w-full max-w-[320px]">
      <div class="flex items-center space-x-2 rtl:space-x-reverse">
         <span class="text-sm font-semibold text-gray-900 dark:text-white">Bonnie Green</span>
         <span class="text-sm font-normal text-gray-500 dark:text-gray-400">11:46</span>
      </div>
      <div class="flex flex-col leading-1.5 p-4 border-gray-200 bg-gray-100 rounded-e-xl rounded-es-xl dark:bg-gray-700">
         <p class="text-sm font-normal text-gray-900 dark:text-white"> That's awesome. I think our users will really appreciate the improvements.</p>
      </div>
      <span class="text-sm font-normal text-gray-500 dark:text-gray-400">Delivered</span>
   </div>
</div>

      ) : (
        <li className="max-w-lg flex gap-x-2 sm:gap-x-4 me-11">
          <img
            className="inline-block h-9 w-9 rounded-full"
            src={user?.imageUrl}
            alt="Image Description"
          />

          <div className="bg-white border border-gray-200 rounded-tl-sm rounded-tr-lg rounded-br-lg rounded-bl-lg p-3 space-y-3">
            <p className="text-sm text-gray-800">{content}</p>
          </div>
        </li>
      )}
    </>
  );
};

export default Message;
