import PlusIcon from "@heroicons/react/24/outline/PlusIcon";

const CreateChannelButton: React.FC = () => {
  const [creatingChannel, setCreatingChannel] = React.useState(false);
  const [channelName, setChannelName] = React.useState("");

  const handleCreateChannel = () => {
    setCreatingChannel(true);
  };

  const handleChannelNameChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setChannelName(event.target.value);
  };

  const handleSubmitChannel = () => {
    if (channelName.trim() === "") {
      alert("Channel name cannot be empty.");
      return;
    }
    // Here you would typically call an API to create the channel
    console.log("Channel created with name:", channelName);
    setCreatingChannel(false);
    setChannelName("");
  };

  const creationInput = (
    <div className="flex gap-x-2">
      <input
        type="text"
        placeholder="Channel name"
        value={channelName}
        onChange={handleChannelNameChange}
        className="rounded-md border border-gray-300 p-2 text-sm leading-6"
      />
      <button
        className="bg-indigo-600 text-white rounded-md p-2 text-sm leading-6"
        onClick={handleSubmitChannel}
      >
        Submit
      </button>
    </div>
  );
  return (
    <button
        className="text-indigo-600 hover:bg-indigo-50 group flex gap-x-3 rounded-md p-2 text-sm leading-6 font-semibold mt-2 w-full"
        onClick={handleCreateChannel}
    >
      <PlusIcon className="text-indigo-600 h-6 w-6 shrink-0" />
      Create a channel
    </button>
  );
};

export default CreateChannelButton;
