interface MessageProps {
  userId: string;
  messageContent: string;
  timestamp: Date;
}

const Message: React.FC<MessageProps> = ({ userId, messageContent, timestamp }) => {
  return (
    <>
      <li className="max-w-lg flex gap-x-2 sm:gap-x-4 me-11">
        {/* User Avatar */}
        <img
          className="inline-block h-9 w-9 rounded-full"
          src={`https://avatars.dicebear.com/api/initials/${userId}.svg`}
          alt={`${userId}'s avatar`}
        />

        {/* Message Card */}
        <div className="bg-white border border-gray-200 rounded-2xl p-4 space-y-3">
          <div className="space-y-1.5">
            <p className="text-sm text-gray-800">{messageContent}</p>
            <p className="text-xs text-gray-500">{timestamp.toLocaleString()}</p>
          </div>
        </div>
        {/* End Message Card */}
      </li>
    </>
  );
};

export default Message;
