import { useUser } from "@clerk/clerk-react";

interface MessageProps {
  sender: string;
  content: string;
  timestamp: Date;
}

const Message: React.FC<MessageProps> = ({ sender, content, timestamp }) => {
  const { user } = useUser();
  const isCurrentUser = user?.id === sender;

  const messageBoxClass = "flex flex-col gap-1 w-full max-w-[320px]";
  const messageContentClass = "flex flex-col leading-1.5 p-4 text-sm font-normal text-gray-900";
  const messageDetailsClass = "flex items-center space-x-2 rtl:space-x-reverse text-sm font-normal text-gray-500";
  const deliveredStatusClass = "text-xs font-normal text-gray-500";
  const currentUserClasses = "justify-end items-end border-indigo-200 bg-indigo-100 rounded-tl-xl rounded-bl-xl rounded-br-xl";
  const otherUserClasses = "items-start border-gray-200 bg-gray-100 rounded-e-xl rounded-es-xl";

  return (
    <>
      {isCurrentUser ? (
        <>
          <div className="flex justify-end items-start gap-2.5">
            <div className="flex flex-col gap-1 w-full max-w-[320px] items-end">
              <div className="flex items-center space-x-2 rtl:space-x-reverse">
                <span className="text-sm font-semibold text-indigo-600">
                  Bonnie Green
                </span>
                <span className="text-sm font-normal text-gray-500">11:46</span>
              </div>
              <div className="flex flex-col leading-1.5 p-4 border-indigo-200 bg-indigo-100 rounded-tl-xl rounded-bl-xl rounded-br-xl">
                <p className="text-sm font-normal text-gray-900">{content}</p>
              </div>
              <span className="text-xs font-normal text-gray-500">
                Delivered
              </span>
            </div>
          </div>
        </>
      ) : (
        <div className="flex items-start gap-2.5">
          <div className="flex flex-col gap-1 w-full max-w-[320px]">
            <div className="flex items-center space-x-2 rtl:space-x-reverse">
              <span className="text-sm font-semibold text-gray-900">
                Bonnie Green
              </span>
              <span className="text-sm font-normal text-gray-500">11:46</span>
            </div>
            <div className="flex flex-col leading-1.5 p-4 border-gray-200 bg-gray-100 rounded-e-xl rounded-es-xl">
              <p className="text-sm font-normal text-gray-900">
                {" "}
                That's awesome. I think our users will really appreciate the
                improvements.
              </p>
            </div>
            <span className="text-xs font-normal text-gray-500">Delivered</span>
          </div>
        </div>
      )}
    </>
  );
};

export default Message;
