import { useEffect, useState } from "react";
import {
  QuerySnapshot,
  collection,
  getDocs,
  onSnapshot,
  orderBy,
  query,
} from "firebase/firestore";
import Message from "./Message";
import db from "../services/firebaseConfig";

const MessageList: React.FC = () => {
  const [messages, setMessages] = useState<
    Array<{ content: any; sender: any; timestamp: any }>
  >([]);

  useEffect(() => {
    const messagesCollection = collection(db, "messages");
    const messagesQuery = query(messagesCollection, orderBy("timestamp"));

    // Fetch all messages once on component mount
    getDocs(messagesQuery).then((snapshot: QuerySnapshot) => {
      const initialMessages = snapshot.docs.map((doc) => ({
        content: doc.data().content,
        sender: doc.data().senderId,
        timestamp: doc.data().timestamp.toDate().toLocaleTimeString("en-US", {
          hour: 'numeric',
          minute: '2-digit',
          hour12: true
        }),
      }));
      console.log(initialMessages)
      setMessages(initialMessages);
    });

    // Subscribe to changes after initial fetch
    const unsubscribe = onSnapshot(messagesQuery, (snapshot: QuerySnapshot) => {
      const updatedMessages = snapshot.docs.map((doc) => ({
        content: doc.data().content,
        sender: doc.data().senderId,
        timestamp: doc.data().timestamp.toDate().toLocaleTimeString("en-US", {
          hour: 'numeric',
          minute: '2-digit',
          hour12: true
        }),
      }));
      setMessages(updatedMessages);
    });

    // Cleanup subscription on component unmount
    return () => unsubscribe();
  }, []);

  return (
    <div className="flex flex-col h-full overflow-auto mb-4">
      <ul className="space-y-5 mt-auto">
        {messages.map((message, i) => {
          return (
            <Message
              key={i}
              sender={message.sender}
              content={message.content}
              timestamp={message.timestamp}
            />
          );
        })}
      </ul>
    </div>
  );
};

export default MessageList;
