import { useUser } from "@clerk/clerk-react";
import { clerkClient, createClerkClient, User } from "@clerk/clerk-sdk-node";
import { useEffect, useState } from "react";

interface MessageProps {
  sender: string;
  content: string;
  timestamp: Date;
}

const Message: React.FC<MessageProps> = ({ sender, content, timestamp }) => {
  const [otherUser, setOtherUser] = useState<User | null>(null);
  const { user } = useUser();
  const isCurrentUser = user?.id === sender;

  const clerk = createClerkClient({ secretKey: '...' });

  useEffect(() => {
    if (sender == user?.id) return;

    const fetchUserName = async () => {
      const response = await clerkClient.users.getUser(sender);
      setOtherUser(response);
    };

    fetchUserName();
  }, []);

  if (otherUser == null) return null;

  return isCurrentUser ? (
    <div className="flex items-start gap-2.5 justify-end">
      <div className="flex flex-col gap-1 max-w-[320px] items-end">
        <div className="flex items-center space-x-2 rtl:space-x-reverse">
          <span className="text-sm font-semibold text-indigo-900">
            {user.username}
          </span>
          <span className="text-xs font-normal text-gray-500">
            {timestamp.toString()}
          </span>
        </div>
        <div className="flex flex-col leading-1.5 p-4 border-indigo-200 bg-indigo-100 rounded-b-xl rounded-tl-xl rounded-bl-xl">
          <p className="text-sm font-normal text-gray-900"> {content}</p>
        </div>
        {/* <span className="text-xs font-normal text-gray-500">Delivered</span> */}
      </div>
    </div>
  ) : (
    <div className="flex items-start gap-2.5">
      <div className="flex flex-col gap-1 max-w-[320px]">
        <div className="flex items-center space-x-2 rtl:space-x-reverse">
          <span className="text-sm font-semibold text-gray-900">
            {otherUser.username}
          </span>
          <span className="text-xs font-normal text-gray-500">
            {timestamp.toString()}
          </span>
        </div>
        <div className="flex flex-col leading-1.5 p-4 border-gray-200 bg-gray-100 rounded-e-xl rounded-es-xl">
          <p className="text-sm font-normal text-gray-900"> {content}</p>
        </div>
        {/* <span className="text-xs font-normal text-gray-500">Delivered</span> */}
      </div>
    </div>
  );
};

export default Message;
